import React, { useState } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import dna from '../assets/img/dna.svg'; // Add your resume image

export const Resume = () => {
  const [isHovered, setIsHovered] = useState(false);

  const handleClick = () => {
    window.open('https://your-resume-link.com', '_blank');
  };

  return (
    <section className="resume" id="resume">
      <Container>
        <Row>
          <Col>
            <h2>My Resume</h2>
            <div 
              className={`resume-box ${isHovered ? 'hovered' : ''}`}
              onMouseEnter={() => setIsHovered(true)}
              onMouseLeave={() => setIsHovered(false)}
              onClick={handleClick}
            >
              <img src={dna} alt="Resume Preview" className="resume-image" />
              <div className="resume-overlay">
                <h3>Click to view full resume</h3>
              </div>
            </div>
          </Col>
        </Row>
      </Container>
    </section>
  );
};
